import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';

const GoogleCalendarCallback = () => {
  const location = useLocation();

  useEffect(() => {
    const handleCallback = async () => {
      const urlParams = new URLSearchParams(location.search);
      const code = urlParams.get('code');
      const state = urlParams.get('state');
      const error = urlParams.get('error');

      if (error) {
        console.error('OAuth error:', error);
        window.close();
        return;
      }

      if (code) {
        try {
          // Exchange code for tokens
          const response = await fetch('/api/v1/calendar/exchange-token', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              authorization_code: code,
              state: state
            }),
          });

          const data = await response.json();

          if (data.success) {
            // Store credentials in localStorage for the parent window to pick up
            localStorage.setItem('google_calendar_credentials', JSON.stringify(data.credentials));

            // Close the popup window
            window.close();
          } else {
            console.error('Failed to exchange token:', data);
            window.close();
          }
        } catch (err) {
          console.error('Error exchanging token:', err);
          window.close();
        }
      }
    };

    handleCallback();
  }, [location]);

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="text-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
        <p className="text-gray-600">Completing Google Calendar authorization...</p>
      </div>
    </div>
  );
};

export default GoogleCalendarCallback;
