import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Pill, 
  Activity, 
  AlertCircle, 
  CheckCircle, 
  Clock, 
  Calendar,
  TrendingUp,
  Heart,
  RefreshCw,
  Eye,
  Edit,
  Plus
} from 'lucide-react';
import { prescriptionAPI } from '../services/apiService';
import { useHealth } from '../contexts/HealthContext';

const PrescriptionDashboard = () => {
  const { user } = useHealth();
  const [dashboardData, setDashboardData] = useState({
    medications: [],
    conditions: [],
    recommendations: [],
    recent_prescriptions: [],
    summary: {}
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [refreshing, setRefreshing] = useState(false);
  const [selectedTab, setSelectedTab] = useState('overview');

  useEffect(() => {
    if (user?.id) {
      fetchDashboardData();
    }
  }, [user]);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await prescriptionAPI.getDashboardData(user.id);
      setDashboardData(response);
    } catch (err) {
      setError('Failed to load dashboard data: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleRefresh = async () => {
    setRefreshing(true);
    await fetchDashboardData();
    setRefreshing(false);
  };

  const renderSummaryCards = () => (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="glass-card rounded-xl p-6"
      >
        <div className="flex items-center justify-between">
          <div>
            <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Active Medications</p>
            <p className="text-2xl font-bold text-blue-600 dark:text-blue-400">
              {dashboardData.summary.total_medications || 0}
            </p>
          </div>
          <div className="w-12 h-12 bg-blue-100 dark:bg-blue-900/20 rounded-lg flex items-center justify-center">
            <Pill className="w-6 h-6 text-blue-600 dark:text-blue-400" />
          </div>
        </div>
      </motion.div>

      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.1 }}
        className="glass-card rounded-xl p-6"
      >
        <div className="flex items-center justify-between">
          <div>
            <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Active Conditions</p>
            <p className="text-2xl font-bold text-red-600 dark:text-red-400">
              {dashboardData.summary.active_conditions || 0}
            </p>
          </div>
          <div className="w-12 h-12 bg-red-100 dark:bg-red-900/20 rounded-lg flex items-center justify-center">
            <Heart className="w-6 h-6 text-red-600 dark:text-red-400" />
          </div>
        </div>
      </motion.div>

      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.2 }}
        className="glass-card rounded-xl p-6"
      >
        <div className="flex items-center justify-between">
          <div>
            <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Pending Recommendations</p>
            <p className="text-2xl font-bold text-green-600 dark:text-green-400">
              {dashboardData.summary.pending_recommendations || 0}
            </p>
          </div>
          <div className="w-12 h-12 bg-green-100 dark:bg-green-900/20 rounded-lg flex items-center justify-center">
            <TrendingUp className="w-6 h-6 text-green-600 dark:text-green-400" />
          </div>
        </div>
      </motion.div>

      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.3 }}
        className="glass-card rounded-xl p-6"
      >
        <div className="flex items-center justify-between">
          <div>
            <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Last Prescription</p>
            <p className="text-sm font-medium text-gray-800 dark:text-gray-200">
              {dashboardData.summary.last_prescription 
                ? new Date(dashboardData.summary.last_prescription).toLocaleDateString()
                : 'None'
              }
            </p>
          </div>
          <div className="w-12 h-12 bg-purple-100 dark:bg-purple-900/20 rounded-lg flex items-center justify-center">
            <Calendar className="w-6 h-6 text-purple-600 dark:text-purple-400" />
          </div>
        </div>
      </motion.div>
    </div>
  );

  const renderMedications = () => (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="glass-card rounded-xl p-6 mb-6"
    >
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-xl font-semibold text-gray-800 dark:text-gray-200 flex items-center">
          <Pill className="w-6 h-6 mr-2 text-blue-600" />
          Current Medications
        </h3>
        <button className="btn-sm btn-primary flex items-center space-x-2">
          <Plus className="w-4 h-4" />
          <span>Add Medication</span>
        </button>
      </div>

      <div className="space-y-4">
        {dashboardData.medications.length > 0 ? (
          dashboardData.medications.map((medication, index) => (
            <div key={index} className="bg-blue-50 dark:bg-blue-900/20 rounded-lg p-4">
              <div className="flex items-center justify-between mb-2">
                <h4 className="font-medium text-blue-800 dark:text-blue-200">
                  {medication.medication?.name || 'Unknown Medication'}
                </h4>
                <div className="flex items-center space-x-2">
                  <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                    medication.status === 'active' 
                      ? 'bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-300'
                      : 'bg-gray-100 text-gray-800 dark:bg-gray-900/20 dark:text-gray-300'
                  }`}>
                    {medication.status || 'Active'}
                  </span>
                  <button className="text-gray-400 hover:text-gray-600">
                    <Edit className="w-4 h-4" />
                  </button>
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
                <div>
                  <span className="text-gray-600 dark:text-gray-400">Dosage:</span>
                  <span className="ml-2 text-gray-800 dark:text-gray-200">
                    {medication.medication?.dosage || 'Not specified'}
                  </span>
                </div>
                <div>
                  <span className="text-gray-600 dark:text-gray-400">Frequency:</span>
                  <span className="ml-2 text-gray-800 dark:text-gray-200">
                    {medication.medication?.frequency || 'Not specified'}
                  </span>
                </div>
                <div>
                  <span className="text-gray-600 dark:text-gray-400">Next Dose:</span>
                  <span className="ml-2 text-gray-800 dark:text-gray-200">
                    {medication.next_dose || 'Calculate'}
                  </span>
                </div>
              </div>

              {medication.medication?.instructions && (
                <div className="mt-2 text-sm text-gray-600 dark:text-gray-400">
                  <span className="font-medium">Instructions:</span> {medication.medication.instructions}
                </div>
              )}
            </div>
          ))
        ) : (
          <div className="text-center py-8 text-gray-500 dark:text-gray-400">
            <Pill className="w-12 h-12 mx-auto mb-4 opacity-50" />
            <p>No medications found. Upload a prescription to get started.</p>
          </div>
        )}
      </div>
    </motion.div>
  );

  const renderConditions = () => (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="glass-card rounded-xl p-6 mb-6"
    >
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-xl font-semibold text-gray-800 dark:text-gray-200 flex items-center">
          <Heart className="w-6 h-6 mr-2 text-red-600" />
          Health Conditions
        </h3>
      </div>

      <div className="space-y-4">
        {dashboardData.conditions.length > 0 ? (
          dashboardData.conditions.map((condition, index) => (
            <div key={index} className="bg-red-50 dark:bg-red-900/20 rounded-lg p-4">
              <div className="flex items-center justify-between mb-2">
                <h4 className="font-medium text-red-800 dark:text-red-200">
                  {condition.condition?.name || 'Unknown Condition'}
                </h4>
                <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                  condition.severity === 'high' 
                    ? 'bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-300'
                    : condition.severity === 'medium'
                    ? 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900/20 dark:text-yellow-300'
                    : 'bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-300'
                }`}>
                  {condition.severity || 'Moderate'}
                </span>
              </div>

              {condition.condition?.description && (
                <p className="text-sm text-gray-600 dark:text-gray-400 mb-2">
                  {condition.condition.description}
                </p>
              )}

              {condition.management_notes && (
                <div className="text-sm text-gray-600 dark:text-gray-400">
                  <span className="font-medium">Management:</span> {condition.management_notes}
                </div>
              )}
            </div>
          ))
        ) : (
          <div className="text-center py-8 text-gray-500 dark:text-gray-400">
            <Heart className="w-12 h-12 mx-auto mb-4 opacity-50" />
            <p>No conditions detected. Upload a prescription for analysis.</p>
          </div>
        )}
      </div>
    </motion.div>
  );

  const renderRecommendations = () => (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="glass-card rounded-xl p-6 mb-6"
    >
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-xl font-semibold text-gray-800 dark:text-gray-200 flex items-center">
          <TrendingUp className="w-6 h-6 mr-2 text-green-600" />
          Health Recommendations
        </h3>
      </div>

      <div className="space-y-4">
        {dashboardData.recommendations.length > 0 ? (
          dashboardData.recommendations.map((recommendation, index) => (
            <div key={recommendation.id} className="bg-green-50 dark:bg-green-900/20 rounded-lg p-4">
              <div className="flex items-center justify-between mb-2">
                <h4 className="font-medium text-green-800 dark:text-green-200">
                  {recommendation.title}
                </h4>
                <div className="flex items-center space-x-2">
                  <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                    recommendation.priority === 'high' 
                      ? 'bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-300'
                      : recommendation.priority === 'medium'
                      ? 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900/20 dark:text-yellow-300'
                      : 'bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-300'
                  }`}>
                    {recommendation.priority} priority
                  </span>
                  <span className="px-2 py-1 bg-blue-100 text-blue-800 dark:bg-blue-900/20 dark:text-blue-300 rounded-full text-xs font-medium">
                    {recommendation.type}
                  </span>
                </div>
              </div>

              <p className="text-sm text-gray-600 dark:text-gray-400 mb-3">
                {recommendation.description}
              </p>

              {recommendation.action_items && recommendation.action_items.length > 0 && (
                <div className="mb-3">
                  <h5 className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">Action Items:</h5>
                  <ul className="space-y-1">
                    {recommendation.action_items.map((item, itemIndex) => (
                      <li key={itemIndex} className="flex items-center text-sm text-gray-600 dark:text-gray-400">
                        <CheckCircle className="w-4 h-4 mr-2 text-green-500" />
                        {item}
                      </li>
                    ))}
                  </ul>
                </div>
              )}

              {recommendation.timeline && (
                <div className="flex items-center text-sm text-gray-500 dark:text-gray-400">
                  <Clock className="w-4 h-4 mr-1" />
                  Timeline: {recommendation.timeline}
                </div>
              )}
            </div>
          ))
        ) : (
          <div className="text-center py-8 text-gray-500 dark:text-gray-400">
            <TrendingUp className="w-12 h-12 mx-auto mb-4 opacity-50" />
            <p>No recommendations available. Upload a prescription to get personalized health recommendations.</p>
          </div>
        )}
      </div>
    </motion.div>
  );

  const renderTabContent = () => {
    switch (selectedTab) {
      case 'medications':
        return renderMedications();
      case 'conditions':
        return renderConditions();
      case 'recommendations':
        return renderRecommendations();
      default:
        return (
          <>
            {renderMedications()}
            {renderConditions()}
            {renderRecommendations()}
          </>
        );
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto"></div>
          <p className="mt-4 text-gray-600 dark:text-gray-400">Loading prescription data...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto p-6">
      <div className="flex items-center justify-between mb-8">
        <div>
          <h2 className="text-3xl font-bold text-gray-800 dark:text-gray-200">
            Prescription Dashboard
          </h2>
          <p className="text-gray-600 dark:text-gray-400 mt-1">
            Real-time view of your medications, conditions, and health recommendations
          </p>
        </div>
        <button
          onClick={handleRefresh}
          disabled={refreshing}
          className="btn-primary flex items-center space-x-2"
        >
          <RefreshCw className={`w-4 h-4 ${refreshing ? 'animate-spin' : ''}`} />
          <span>Refresh</span>
        </button>
      </div>

      {error && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          className="mb-6 p-4 bg-red-50 dark:bg-red-900/20 rounded-lg border border-red-200 dark:border-red-800"
        >
          <div className="flex items-center space-x-2 text-red-700 dark:text-red-300">
            <AlertCircle className="w-5 h-5" />
            <span>{error}</span>
          </div>
        </motion.div>
      )}

      {renderSummaryCards()}

      {/* Tab Navigation */}
      <div className="flex space-x-1 mb-6 bg-gray-100 dark:bg-gray-800 rounded-lg p-1">
        {[
          { id: 'overview', label: 'Overview', icon: Eye },
          { id: 'medications', label: 'Medications', icon: Pill },
          { id: 'conditions', label: 'Conditions', icon: Heart },
          { id: 'recommendations', label: 'Recommendations', icon: TrendingUp }
        ].map((tab) => {
          const Icon = tab.icon;
          return (
            <button
              key={tab.id}
              onClick={() => setSelectedTab(tab.id)}
              className={`flex items-center space-x-2 px-4 py-2 rounded-md transition-colors ${
                selectedTab === tab.id
                  ? 'bg-white dark:bg-gray-700 text-primary-600 dark:text-primary-400 shadow-sm'
                  : 'text-gray-600 dark:text-gray-400 hover:text-gray-800 dark:hover:text-gray-200'
              }`}
            >
              <Icon className="w-4 h-4" />
              <span>{tab.label}</span>
            </button>
          );
        })}
      </div>

      {renderTabContent()}
    </div>
  );
};

export default PrescriptionDashboard;
