import React, { useState } from 'react';
import './Registration.css';

const Registration = ({ onRegistrationSuccess, onSwitchToLogin }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    first_name: '',
    last_name: '',
    date_of_birth: '',
    gender: '',
    medical_conditions: '',
    allergies: '',
    current_medications: '',
    emergency_contact: ''
  });

  const [currentStep, setCurrentStep] = useState(1);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    setError(''); // Clear error when user types
  };

  const validateStep1 = () => {
    if (!formData.email || !formData.password || !formData.confirmPassword) {
      setError('Please fill in all required fields');
      return false;
    }

    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      return false;
    }

    if (formData.password.length < 8) {
      setError('Password must be at least 8 characters long');
      return false;
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.email)) {
      setError('Please enter a valid email address');
      return false;
    }

    return true;
  };

  const validateStep2 = () => {
    if (!formData.first_name || !formData.last_name) {
      setError('Please enter your first and last name');
      return false;
    }
    return true;
  };

  const handleNextStep = () => {
    if (currentStep === 1 && validateStep1()) {
      setCurrentStep(2);
    } else if (currentStep === 2 && validateStep2()) {
      setCurrentStep(3);
    }
  };

  const handlePrevStep = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateStep2()) {
      return;
    }

    setLoading(true);
    setError('');

    try {
      const registrationData = {
        email: formData.email,
        password: formData.password,
        first_name: formData.first_name,
        last_name: formData.last_name,
        date_of_birth: formData.date_of_birth || null,
        gender: formData.gender || null,
        medical_conditions: formData.medical_conditions || null,
        allergies: formData.allergies || null,
        current_medications: formData.current_medications || null,
        emergency_contact: formData.emergency_contact || null,
        role: 'PATIENT'
      };

      const response = await fetch('/api/v1/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(registrationData)
      });

      if (response.ok) {
        const userData = await response.json();
        setSuccess('Registration successful! You can now log in.');

        // Call success callback if provided
        if (onRegistrationSuccess) {
          onRegistrationSuccess(userData);
        }

        // Reset form
        setFormData({
          email: '',
          password: '',
          confirmPassword: '',
          first_name: '',
          last_name: '',
          date_of_birth: '',
          gender: '',
          medical_conditions: '',
          allergies: '',
          current_medications: '',
          emergency_contact: ''
        });
        setCurrentStep(1);

        // Auto-switch to login after 2 seconds
        setTimeout(() => {
          if (onSwitchToLogin) {
            onSwitchToLogin();
          }
        }, 2000);

      } else {
        const errorData = await response.json();
        setError(errorData.detail || 'Registration failed. Please try again.');
      }
    } catch (err) {
      setError('Network error. Please check your connection and try again.');
    } finally {
      setLoading(false);
    }
  };

  const renderStep1 = () => (
    <div className="registration-step">
      <h3>Account Information</h3>
      <div className="form-group">
        <label htmlFor="email">Email Address *</label>
        <input
          type="email"
          id="email"
          name="email"
          value={formData.email}
          onChange={handleInputChange}
          placeholder="Enter your email address"
          required
        />
      </div>

      <div className="form-group">
        <label htmlFor="password">Password *</label>
        <input
          type="password"
          id="password"
          name="password"
          value={formData.password}
          onChange={handleInputChange}
          placeholder="Enter a strong password (min 8 characters)"
          required
        />
      </div>

      <div className="form-group">
        <label htmlFor="confirmPassword">Confirm Password *</label>
        <input
          type="password"
          id="confirmPassword"
          name="confirmPassword"
          value={formData.confirmPassword}
          onChange={handleInputChange}
          placeholder="Confirm your password"
          required
        />
      </div>
    </div>
  );

  const renderStep2 = () => (
    <div className="registration-step">
      <h3>Personal Information</h3>
      <div className="form-row">
        <div className="form-group">
          <label htmlFor="first_name">First Name *</label>
          <input
            type="text"
            id="first_name"
            name="first_name"
            value={formData.first_name}
            onChange={handleInputChange}
            placeholder="Enter your first name"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="last_name">Last Name *</label>
          <input
            type="text"
            id="last_name"
            name="last_name"
            value={formData.last_name}
            onChange={handleInputChange}
            placeholder="Enter your last name"
            required
          />
        </div>
      </div>

      <div className="form-row">
        <div className="form-group">
          <label htmlFor="date_of_birth">Date of Birth</label>
          <input
            type="date"
            id="date_of_birth"
            name="date_of_birth"
            value={formData.date_of_birth}
            onChange={handleInputChange}
          />
        </div>

        <div className="form-group">
          <label htmlFor="gender">Gender</label>
          <select
            id="gender"
            name="gender"
            value={formData.gender}
            onChange={handleInputChange}
          >
            <option value="">Select gender</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
            <option value="other">Other</option>
            <option value="prefer_not_to_say">Prefer not to say</option>
          </select>
        </div>
      </div>

      <div className="form-group">
        <label htmlFor="emergency_contact">Emergency Contact</label>
        <input
          type="text"
          id="emergency_contact"
          name="emergency_contact"
          value={formData.emergency_contact}
          onChange={handleInputChange}
          placeholder="Emergency contact name and phone number"
        />
      </div>
    </div>
  );

  const renderStep3 = () => (
    <div className="registration-step">
      <h3>Medical History</h3>
      <p className="step-description">
        This information helps us provide personalized health recommendations. All medical information is encrypted and secure.
      </p>

      <div className="form-group">
        <label htmlFor="medical_conditions">Medical Conditions</label>
        <textarea
          id="medical_conditions"
          name="medical_conditions"
          value={formData.medical_conditions}
          onChange={handleInputChange}
          placeholder="List any medical conditions (e.g., diabetes, hypertension, asthma)"
          rows="3"
        />
      </div>

      <div className="form-group">
        <label htmlFor="allergies">Allergies</label>
        <textarea
          id="allergies"
          name="allergies"
          value={formData.allergies}
          onChange={handleInputChange}
          placeholder="List any allergies (food, medication, environmental)"
          rows="3"
        />
      </div>

      <div className="form-group">
        <label htmlFor="current_medications">Current Medications</label>
        <textarea
          id="current_medications"
          name="current_medications"
          value={formData.current_medications}
          onChange={handleInputChange}
          placeholder="List current medications and dosages"
          rows="3"
        />
      </div>

      <div className="privacy-notice">
        <p>üîí <strong>Privacy Notice:</strong> Your medical information is encrypted and will only be used to provide personalized health recommendations. We comply with all healthcare privacy regulations.</p>
      </div>
    </div>
  );

  return (
    <div className="registration-container">
      <div className="registration-card">
        <div className="registration-header">
          <h2>üè• Create Your Health Account</h2>
          <p>Join our comprehensive health management system</p>
        </div>

        <div className="step-indicator">
          <div className={`step ${currentStep >= 1 ? 'active' : ''} ${currentStep > 1 ? 'completed' : ''}`}>
            <span className="step-number">1</span>
            <span className="step-label">Account</span>
          </div>
          <div className={`step ${currentStep >= 2 ? 'active' : ''} ${currentStep > 2 ? 'completed' : ''}`}>
            <span className="step-number">2</span>
            <span className="step-label">Personal</span>
          </div>
          <div className={`step ${currentStep >= 3 ? 'active' : ''}`}>
            <span className="step-number">3</span>
            <span className="step-label">Medical</span>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="registration-form">
          {currentStep === 1 && renderStep1()}
          {currentStep === 2 && renderStep2()}
          {currentStep === 3 && renderStep3()}

          {error && (
            <div className="error-message">
              <p>‚ùå {error}</p>
            </div>
          )}

          {success && (
            <div className="success-message">
              <p>‚úÖ {success}</p>
            </div>
          )}

          <div className="form-actions">
            {currentStep > 1 && (
              <button
                type="button"
                onClick={handlePrevStep}
                className="btn-secondary"
              >
                Previous
              </button>
            )}

            {currentStep < 3 ? (
              <button
                type="button"
                onClick={handleNextStep}
                className="btn-primary"
              >
                Next
              </button>
            ) : (
              <button
                type="submit"
                disabled={loading}
                className="btn-primary"
              >
                {loading ? 'Creating Account...' : 'Create Account'}
              </button>
            )}
          </div>
        </form>

        <div className="registration-footer">
          <p>
            Already have an account?{' '}
            <button
              type="button"
              onClick={onSwitchToLogin}
              className="link-button"
            >
              Sign In
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Registration;
